'use strict'

const config = require('../../../data/services-config')
const BreezyRequest = require('../../../src/http/request')
const request = new BreezyRequest(config.Company)
const LOG_NS = '[CORE-COMPANY-SERVICE]'

async function getCompany(companyId, actingUser) {

  let resource = "company/" + companyId

  if (actingUser) {
    resource += "?acting_user_id=" + actingUser._id
  }

  try {
    return await request.get(resource)
  } catch (e) {
    console.error(LOG_NS, e)
  }
}

module.exports = {
  getCompany
}