'use strict'

module.exports = {
  delay: delay
}

/**
 * delays the return of a value via a promise
 * @param {number} ms
 * @param {object} [value]
 * @returns {Promise}
 */
function delay(ms, value) {
  return new Promise(resolve => {
    setTimeout(() => {
      resolve(value)
    }, ms)
  })
}
