'use strict'
const sec_config = require('../../data/security-config')

for (let role of sec_config.defaultRoles) {
  let validated = []
  for (let perm of role.permissions) {
    if (validated.indexOf(perm._id) >= 0) {
      console.error('duplicate permission', perm._id, '@', validated.indexOf(perm._id))
    } else {
      validated.push(perm._id)
    }
  }
  console.log('Validated', validated.length, 'of', role.permissions.length, 'for role', role._id)
}



