const Server = require('../../src/system/server');
const systemRouter = require('../../src/routes/system-routes')
const companyRouter = require('../../src/routes/company-routes')
const positionRouter = require('../../src/routes/position-routes')

let port = 3080;

if (process.argv.length > 2) {
  port = parseInt(process.argv[2]);
}

const breezyAF = new Server(port);
breezyAF.useRoutes('/api/v1', systemRouter)
breezyAF.useRoutes('/api/v1', companyRouter)
breezyAF.start();